const __pageData = JSON.parse('{"title":"Bootloader Driver Installation with Zadig","description":"","frontmatter":{},"headers":[],"relativePath":"driver_installation_zadig.md","filePath":"driver_installation_zadig.md","lastUpdated":1751656200000}');
